import React from 'react'
import ColorPicker from './ColorPicker'
import ContentLoader from 'react-content-loader'
import dynamic from 'next/dynamic'

const DropzoneWithNoSSR = dynamic(() => import('react-dropzone'), {
  ssr: false,
  loading: () => (
    <ContentLoader height={120} width={640}>
      <rect x='0' y='0' rx='5' ry='5' width='100%' height='100' />
    </ContentLoader>
  )
})

class Input extends React.Component {
  constructor(props) {
    super(props)
    const { value } = this.props
    this.state = {
      value
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.value !== this.props.value) {
      this.setState({ value: nextProps.value })
    }
  }

  handleInputChange = event => {
    const target = event.target
    const value = target.value
    this.handleChange(value)
  }

  handleChange = value => {
    const { onChange = () => {}, name = '' } = this.props
    this.setState({ value }, () => {
      onChange(name, value)
    })
  }

  handleOnDropAccepted = acceptedFiles => {
    const { onChange = () => {} } = this.props
    const file = Object.assign(acceptedFiles[acceptedFiles.length - 1], {
      preview:
        URL && URL.createObjectURL
          ? URL.createObjectURL(acceptedFiles[acceptedFiles.length - 1])
          : typeof window !== 'undefined' && window.webkitURL
          ? window.webkitURL.createObjectURL(acceptedFiles[acceptedFiles.length - 1])
          : null
    })
    onChange('upload', file)
  }

  handleOnDropRejected = rejectedFiles => {
    alert('This file type is not allowed:' + rejectedFiles[rejectedFiles.length - 1].name)
  }

  render() {
    const {
      label,
      inline = true,
      expand = true,
      type = 'text',
      placeholder = '',
      choices = [],
      disabled = false,
      onKeyDown = () => {},
      className
    } = this.props
    const { value = '' } = this.state

    return (
      <div className='inputGroup'>
        {label && <label>{label}</label>}
        {type == 'text' || type == 'password' || type == 'number' ? (
          <input
            className={className}
            type={type}
            placeholder={placeholder}
            defaultvalue={this.state.value}
            onChange={this.handleInputChange}
            disabled={disabled}
            onKeyDown={onKeyDown}
          />
        ) : type == 'select' ? (
          <select onChange={this.handleInputChange} value={value || ''} className={className}>
            <option value={''}>Choose an option...</option>
            {choices.map(choice => (
              <option key={choice.id} value={choice.id}>
                {choice.label}
              </option>
            ))}
          </select>
        ) : type == 'color' ? (
          <ColorPicker color={value} onChange={this.handleChange} />
        ) : type == 'photo' ? (
          <DropzoneWithNoSSR
            accept='image/*,video/*'
            onDropAccepted={this.handleOnDropAccepted}
            onDropRejected={this.handleOnDropRejected}
            multiple={false}
          >
            {({ getRootProps, getInputProps, isDragActive }) => {
              return (
                <div {...getRootProps()} className='upload'>
                  <input {...getInputProps()} />
                  {isDragActive || (!value || value == '') ? (
                    <div className={'photo-upload'}>Drop a photo here...</div>
                  ) : (
                    <div className={'photo'}>
                      <div
                        className={'thumbnail'}
                        style={{
                          backgroundImage: `url(${value})`
                        }}
                      />
                      <span className={'link'}>{value}</span>
                    </div>
                  )}
                </div>
              )
            }}
          </DropzoneWithNoSSR>
        ) : type == 'video' ? (
          <DropzoneWithNoSSR
            accept='video/*'
            onDropAccepted={this.handleOnDropAccepted}
            onDropRejected={this.handleOnDropRejected}
            multiple={false}
          >
            {({ getRootProps, getInputProps, isDragActive }) => {
              return (
                <div {...getRootProps()} className='video'>
                  <input {...getInputProps()} />
                  {isDragActive || (!value || value == '') ? (
                    <div className={'video-upload'}>Drop a photo here...</div>
                  ) : (
                    <div className={'video'}>
                    <div
                      className={'thumbnail'}
                      style={{
                        backgroundImage: `url(${value})`
                      }}
                    />
                      <span className={'link'}>{value}</span>
                    </div>
                  )}
                </div>
              )
            }}
            </DropzoneWithNoSSR>
          ) : (
            <textarea
              onChange={this.handleInputChange}
              defaultvalue={this.state.value}
              placeholder={placeholder}
              className={className}
            />
          )
      }
        <style jsx>{`
          .inputGroup {
            margin-bottom: 16px;
            display: ${!inline ? 'flex' : 'inline-block'};
            flex-direction: ${inline ? 'row' : 'column'};
            justify-content: flex-start;
          }

          label {
            margin-right: 16px;
            color: #878787;
            font-family: 'Open Sans', sans-serif;
            min-width: 100px;
            max-width: ${inline ? '100px' : 'none'};
            display: inline-block;
            padding-top: 16px;
            padding-bottom: ${inline ? '0px' : '16px'};
          }

          input,
          textarea,
          select,
          .video,
          .video-upload,
          .photo,
          .photo-upload {
            font-family: 'Open Sans', sans-serif;
            color: #333;
            background-color: #f7f7f7;
            min-height: 40px;
            min-width: ${expand ? '450px' : '0px'};
            border-radius: 2px;
            border: none;
            outline: none;
            padding: 8px;
            padding-left: 16px;
            padding-right: 16px;
            font-size: 16px;
          }

          input:disabled,
          textarea:disabled,
          select:disabled {
            background-color: #d7d7d7;
            cursor: not-allowed;
          }

          textarea {
            resize: vertical;
            min-height: 100px;
          }

          select {
            -webkit-appearance: none;
            -moz-appearance: none;
            appearance: none;
            padding: 16px;
          }

          input[type='number'] {
            min-width: 50px !important;
            max-width: 50px !important;
            text-align: center;
          }

          .upload {
            display: ${inline ? 'inline-block' : 'flex'};
            cursor: pointer;
            outline: none;
            max-width: 100%;
          }

          .photo
