import { Component } from 'react'
import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faClock, faImages } from '@fortawesome/free-regular-svg-icons'
import { faTrash, faPlay } from '@fortawesome/free-solid-svg-icons'
import Link from 'next/link'
import { view } from 'react-easy-state'

import { deleteSlideshow } from '../../actions/slideshow'
import { display } from '../../stores'

class SlideshowCard extends Component {
  render() {
    const { value, refresh = () => {} } = this.props
    return (
      <Link href={'/slideshow/' + value._id + '?display=' + display.id}>
        <div className='card'>
          <div className='left'>
            <div
              className={'thumbnail'}
              style={{
                // backgroundImage: `url(${value.data})`,
                backgroundColor: 'gray'
              }}
            >
              <FontAwesomeIcon icon={faPlay} fixedWidth size='lg' color='#FFFFFF' />
            </div>
          </div>
          <div className='middle'>
            <div className='title'>{value.title || '제목 없음'}</div>
            <div className='info'>
              <div className='duration'>
                <div className='icon'>
                  <FontAwesomeIcon icon={faClock} fixedWidth color='#878787' />
                </div>
                <span className='text'>
                  {value.slides.reduce((acc, slide) => acc + slide.duration, 0)}s
                </span>
              </div>
              <div className='slidenum'>
                <div className='icon'>
                  <FontAwesomeIcon icon={faImages} fixedWidth color='#878787' />
                </div>
                <span className='text'>{value.slides.length}</span>
              </div>
            </div>
          </div>
          <div className='right'>
            <div className='actionIcon'>
              <FontAwesomeIcon
                icon={faTrash}
                fixedWidth
                color='#828282'
                onClick={e => {
                  if (e) e.stopPropagation()
                  deleteSlideshow(value._id).then(refresh)
                }}
              />
            </div>
          </div>
          <style jsx>
            {`
              .card {
                padding: 12px;
                font-family: 'Open Sans', sans-serif;
                border-radius: 20px;
                cursor: pointer;
                background: white;
                margin-top: 40px;
                margin-bottom: 40px;
                display: flex;
                flex-direction: row;
                justify-content: center;
                position: relative;
                z-index: 1;
              }

              .title {
                font-family: 'Open Sans', sans-serif;
                font-size: 16px;
                overflow: hidden;
                white-space: nowrap;
                text-overflow: ellipsis;
                color: #4f4f4f;
                margin-bottom: 8px;
              }

              .left {
                font-family: 'Open Sans', sans-serif;
                justify-content: center;
                padding-left: 8px;
                padding-right: 8px;
              }

              .info {
                display: flex;
                flex-direction: row;
              }

              .duration,
              .slidenum {
                font-family: 'Open Sans', sans-serif;
                font-size: 14px;
                color: #878787;
              }

              .duration .icon,
              .slidenum .icon {
                margin-right: 4px;
                display: inline;
                vertical-align: middle;
              }

              .duration .text,
              .slidenum .text {
                vertical-align: middle;
              }

              .duration {
                margin-right: 12px;
              }

              .middle {
                font-family: 'Open Sans', sans-serif;
                display: flex;
                flex-direction: column;
                justify-content: center;
                padding-left: 8px;
                padding-right: 8px;
                flex: 1;
                min-width: 0;
              }

              .right {
                display: flex;
                flex-direction: row;
                font-family: 'Open Sans', sans-serif;
                justify-content: center;
                align-items: center;
                padding-left: 8px;
                padding-right: 8px;
              }

              .thumbnail {
                height: 60px;
                width: 60px;
                border-radius: 2px;
                background-size: cover;
                display: flex;
                justify-content: center;
                align-items: center;
              }

              .actionIcon {
                margin-right: 8px;
                margin-left: 8px;
              }
            `}
          </style>
        </div>
      </Link>
    )
  }
}

export default view(SlideshowCard)
